import 'package:flutter/cupertino.dart';import 'package:flutter/material.dart';import 'package:mobile_bro_food_admin_panel/pages/menu/controller/menu_provider.dart';import 'package:provider/provider.dart';class Detail extends StatelessWidget {  Detail({super.key});  @override  Widget build(BuildContext context) {    final provider = context.watch<MenuProvider>();    return      Visibility(        visible: provider.isAddOpen,        child:Container(          color: Colors.grey,          margin: const EdgeInsets.all(15.0),          padding: const EdgeInsets.all(15.0),          child: SingleChildScrollView(            child: Stack(              alignment: Alignment.topRight,              children: [                Row(children: [                  SizedBox(width: 2,height:  MediaQuery.of(context)                      .size.height * .7,),                  Expanded(child: Column(                    mainAxisSize: MainAxisSize.max,                    crossAxisAlignment: CrossAxisAlignment.start,                    children: const [                      ///#Name                      Text('Food Name'),                      SizedBox(                        height: 15,                      ),                      _name(),                      SizedBox(                        height: 15,                      ),                      ///#price                      Text('Food Price'),                      SizedBox(                        height: 15,                      ),                      _price(),                      SizedBox(                        height: 15,                      ),                      ///#type                      Text('Food Type'),                      SizedBox(                        height: 15,                      ),                      _type(),                      SizedBox(                        height: 15,                      ),                      ///#type                      Text('Food About'),                      SizedBox(                        height: 15,                      ),                      _about(),                      SizedBox(                        height: 15,                      ),                      _button(),                    ],                  ),   )       ,         Expanded(child:  _ImageGet(),)                ],),                const _closeButton(),              ],            ),          ),        ),);  }}class _closeButton extends StatelessWidget {  const _closeButton({    Key? key,  }) : super(key: key);  @override  Widget build(BuildContext context) {    final provider = context.read<MenuProvider>();    return IconButton(        mouseCursor:SystemMouseCursors.click ,        onPressed: provider.closeOrOpen, icon: const Icon(Icons        .close));  }}class _button extends StatelessWidget {  const _button({    Key? key,  }) : super(key: key);  @override  Widget build(BuildContext context) {    final provider = context.watch<MenuProvider>();    if(provider.openBy != null){      return Center(child: provider.isEdit?const CircularProgressIndicator():ElevatedButton(        onPressed:provider.updateItem,        child: const Text(          'Add',        ),      ));    }    return Center(child:provider.isEdit?const CircularProgressIndicator(): ElevatedButton(      onPressed: ()async=>provider.add(context),      child: const Text(        'Add',      ),    ));  }}class _price extends StatelessWidget {  const _price({    Key? key,  }) : super(key: key);  @override  Widget build(BuildContext context) {    final provider = context.read<MenuProvider>();    return TextField(      controller: provider.controllerPrice,      style: const TextStyle(        fontSize: 16,        fontWeight: FontWeight.w600,        color: Colors.black,      ),      decoration: const InputDecoration(hintText:      "price",border: InputBorder.none,filled: true,          fillColor: Colors.white),    );  }}class _name extends StatelessWidget {  const _name({    Key? key,  }) : super(key: key);  @override  Widget build(BuildContext context) {    final provider = context.read<MenuProvider>();    return TextField(      controller: provider.controllerName,      style: const TextStyle(        fontSize: 16,        fontWeight: FontWeight.w600,        color: Colors.black,      ),      decoration: const InputDecoration(hintText:      "Name",border: InputBorder.none,filled: true,          fillColor: Colors.white),    );  }}class _type extends StatelessWidget {  const _type({    Key? key,  }) : super(key: key);  @override  Widget build(BuildContext context) {    final provider = context.read<MenuProvider>();    return DropdownButton(      style:      const TextStyle(fontSize: 22, color: Colors.white),      dropdownColor: Colors.orangeAccent,      icon: const Icon(CupertinoIcons.circle_grid_3x3_fill),      value: provider.type,      hint: const Text(        'Ovqat turini tanlang',      ),      onChanged: provider.dropType,      items: provider.dropDownMenuItems,    );  }}class _about extends StatelessWidget {  const _about({    Key? key,  }) : super(key: key);  @override  Widget build(BuildContext context) {    final provider = context.read<MenuProvider>();    return TextField(      controller: provider.controllerAbout,      style: const TextStyle(        fontSize: 16,        fontWeight: FontWeight.w600,        color: Colors.black,      ),      decoration: const InputDecoration(        hintText: "About",border: InputBorder.none,        filled: true,        fillColor: Colors.white      ),    );  }}class _ImageGet extends StatelessWidget {  @override  Widget build(BuildContext context) {    final provider = context.read<MenuProvider>();    return InkWell(      mouseCursor:SystemMouseCursors.click ,      onTap: () => provider.fileGalery(context),      child: Center(        child: Stack(          alignment: Alignment.bottomRight,          children: [            Material(              borderRadius: BorderRadius.circular(123123),              elevation: 40.0,              shadowColor: Colors.grey.shade400,              child: Container(                clipBehavior: Clip.hardEdge,                decoration: BoxDecoration(                  borderRadius: const BorderRadius.only(                      topLeft: Radius.circular(50),                      bottomLeft: Radius.circular(50)),                  gradient: LinearGradient(                    begin: Alignment.bottomRight,                    colors: [                      Colors.grey.withOpacity(0.6),                      Colors.grey.withOpacity(0.3),                      Colors.grey.withOpacity(0.2),                      Colors.grey.withOpacity(0.05),                    ],                  ),                ),                child: (provider.image != null)                    ? Image.memory(                        provider.image!,                        fit: BoxFit.cover,                        width: 200,                        height: 200,                      )                    : provider.openBy != null? Image.network( provider.openBy!.image):Image.asset                  ('assets/logo/placeholder.png'),              ),            ),            Container(              height: 30,              width: 30,              decoration: BoxDecoration(                  color: Colors.grey,                  borderRadius: BorderRadius.circular(50)),              child: const Icon(                Icons.camera_alt_rounded,                size: 15,                color: Colors.black,              ),            ),          ],        ),      ),    );  }}