import 'package:flutter/cupertino.dart';import 'package:flutter/material.dart';import 'package:mobile_bro_food_admin_panel/pages/menu/controller/menu_provider.dart';import 'package:mobile_bro_food_admin_panel/service/rtdb_service.dart';import 'package:provider/provider.dart';class Detail extends StatelessWidget {  Detail({Key? key}) : super(key: key);  String d = '';  @override  Widget build(BuildContext context) {    final provider = context.watch<MenuProvider>();    return      Visibility(        visible: provider.isAddOpen,        child:Container(          color: Colors.grey,          margin: const EdgeInsets.all(15.0),          padding: const EdgeInsets.all(15.0),          child: SingleChildScrollView(            child: Stack(              alignment: Alignment.topRight,              children: [                Column(                  crossAxisAlignment: CrossAxisAlignment.start,                  children: [                    TextField(                      controller: provider.controllerName,                      style: const TextStyle(                        fontSize: 16,                        fontWeight: FontWeight.w600,                        color: Colors.black,                      ),                      decoration: const InputDecoration(hintText: "Name"),                    ),                    const SizedBox(                      height: 15,                    ),                    TextField(                      controller: provider.controllerPrice,                      style: const TextStyle(                        fontSize: 16,                        fontWeight: FontWeight.w600,                        color: Colors.black,                      ),                      decoration: const InputDecoration(hintText: "price"),                    ),                    const SizedBox(                      height: 15,                    ),                    DropdownButton(                      style:                      const TextStyle(fontSize: 22, color: Colors.white),                      dropdownColor: Colors.orangeAccent,                      icon: const Icon(CupertinoIcons.circle_grid_3x3_fill),                      value: provider.type,                      hint: const Text(                        'Ovqat turini tanlang',                      ),                      onChanged: provider.dropType,                      items: provider.dropDownMenuItems,                    ),                    const SizedBox(                      height: 15,                    ),                    TextField(                      controller: provider.controllerAbout,                      style: const TextStyle(                        fontSize: 16,                        fontWeight: FontWeight.w600,                        color: Colors.black,                      ),                      decoration: const InputDecoration(                        hintText: "About",                      ),                    ),                    const SizedBox(                      height: 15,                    ),                    _ImageGet(),                    ElevatedButton(                      onPressed: ()=>provider.add(context),                      child: const Text(                        'Add',                      ),                    ),                  ],                ),                IconButton(                    mouseCursor:SystemMouseCursors.click ,                    onPressed: provider.closeOrOpen, icon: Icon(Icons                    .close)),              ],            ),          ),        ),);      ;  }}class _ImageGet extends StatelessWidget {  @override  Widget build(BuildContext context) {    final provider = context.read<MenuProvider>();    return InkWell(      mouseCursor:SystemMouseCursors.click ,      onTap: () => provider.fileGalery(context),      child: Center(        child: Stack(          alignment: Alignment.bottomRight,          children: [            Material(              borderRadius: BorderRadius.circular(123123),              elevation: 40.0,              shadowColor: Colors.grey.shade400,              child: Container(                clipBehavior: Clip.hardEdge,                height: 130,                width: 130,                decoration: BoxDecoration(                  borderRadius: const BorderRadius.only(                      topLeft: Radius.circular(50),                      bottomLeft: Radius.circular(50)),                  gradient: LinearGradient(                    begin: Alignment.bottomRight,                    colors: [                      Colors.grey.withOpacity(0.6),                      Colors.grey.withOpacity(0.3),                      Colors.grey.withOpacity(0.2),                      Colors.grey.withOpacity(0.05),                    ],                  ),                ),                child: (provider.image != null)                    ? Image.memory(                        provider.image!,                        fit: BoxFit.cover,                        width: 200,                        height: 200,                      )                    : Image.asset('assets/logo/placeholder.png'),              ),            ),            Container(              height: 30,              width: 30,              decoration: BoxDecoration(                  color: Colors.grey,                  borderRadius: BorderRadius.circular(50)),              child: const Icon(                Icons.camera_alt_rounded,                size: 15,                color: Colors.black,              ),            ),          ],        ),      ),    );  }}