import 'package:flutter/foundation.dart';import 'package:mobile_bro_food_admin_panel/model/Order_model.dart';import 'package:mobile_bro_food_admin_panel/repository/orders_repository.dart';class HistoryProvider {  Stream<List<OrderModel>> get order async* {    List<OrderModel> list = await getOrdersHistory();    while(true){      yield list;      await Future.delayed(const Duration(seconds: 10), () async{        list = await getOrders();      });    }  }}