import 'package:flutter/foundation.dart';import 'package:mobile_bro_food_admin_panel/model/food_model.dart';import 'package:mobile_bro_food_admin_panel/service/net_serivce.dart';class HistoryProvider extends ChangeNotifier{  bool isLoading = true;  List<Foods> list = [];  Future<void> get()async{    Map s =  await NetworkService.GET(NetworkService.API_MENU,{});    if(s.isNotEmpty){      if(list.length != listFromJson(s).length){        list =  listFromJson(s);      }    }else {      list = [];    }    isLoading = false;  }@override  void dispose() {list.clear();super.dispose();  }}