import 'package:flutter/material.dart';import 'package:mobile_bro_food_admin_panel/model/Order_model.dart';import 'package:mobile_bro_food_admin_panel/model/food_model.dart';import 'package:mobile_bro_food_admin_panel/pages/menu/menu_page.dart';import 'package:mobile_bro_food_admin_panel/service/net_serivce.dart';class HomeProvider extends ChangeNotifier {  PageController controller = PageController();  int index = 0;  int page = 0;  List<OrderModel> orders = [];  Future<void> init() async {    Map order =        await NetworkService.GET(NetworkService.API_CREATE_ORDER, {});    if (orders.isNotEmpty) {      orders = listOrder(order);    } else {      orders = [];    }  }  Future<void> post() async {    notifyListeners();  }  void menu(context) {    Navigator.push(        context, MaterialPageRoute(builder: (context) => MenuPage()));  }  void order(int index) async {    page = index;    controller.jumpToPage(page);    notifyListeners();  }  @override  void dispose() {    controller.dispose();    super.dispose();  }}