import 'package:dio/dio.dart';import 'package:flutter/material.dart';import 'package:mobile_bro_food_admin_panel/pages/home/controller/home_controller.dart';import 'package:provider/provider.dart';import '../menu/menu_page.dart';class HomePage extends StatelessWidget {  const HomePage({Key? key}) : super(key: key);  @override  Widget build(BuildContext context) {    return ChangeNotifierProvider(create: (context)=> HomeProvider(),    builder: (_,__){      return _Home();    },    );  }}class _Home extends StatelessWidget{  @override  Widget build(BuildContext context) {    // TODO: implement build    return Scaffold(      appBar: AppBar(      ),      body: IndexedStack(        index: context.read<HomeProvider>().page,        children: [          Column(children: const [            SizedBox(height: 10,),            _cstomTabBar(),          ],),        ],      ),      drawer: _drawer(),    );  }}class _cstomTabBar extends StatelessWidget {  const _cstomTabBar({    Key? key,  }) : super(key: key);  @override  Widget build(BuildContext context) {  final provider =   context.watch<HomeProvider>();    return Row(mainAxisAlignment: MainAxisAlignment.center,children: [      Container(        padding: const EdgeInsets.symmetric(vertical: 10,horizontal:        10),       margin: const EdgeInsets.symmetric(horizontal: 20),       decoration: BoxDecoration        (color: provider.index == 0?Colors.black:Colors.white,      borderRadius: BorderRadius.circular(123123),      ),      child: Text("Activ Buyurtmalar",style: TextStyle(color:      provider.index == 0?Colors.white:Colors.black),),),      Container(        padding: const EdgeInsets.symmetric(vertical: 10,horizontal:        10),       margin: const EdgeInsets.symmetric(horizontal: 20),       decoration: BoxDecoration        (color: provider.index == 1?Colors.black:Colors.grey,      borderRadius: BorderRadius.circular(123123),      ),      child: Text("Buyurtmalar Royhati",style: TextStyle(color:      provider.index == 1?Colors.grey:Colors.black),),),    ],);  }}class _drawer extends StatelessWidget {  final textStyle =  const TextStyle(color: Colors.black,fontSize: 25,);  final buttonStyle = ElevatedButton.styleFrom(      elevation:0,      primary: Colors.transparent  );   _drawer({    Key? key,  }) : super(key: key);  @override  Widget build(BuildContext context) {    final provider =   context.watch<HomeProvider>();    return Drawer(      child: DrawerHeader(        child: Column(          crossAxisAlignment: CrossAxisAlignment.start,          children: [          SizedBox(height: 20,),          ElevatedButton.icon(            style: buttonStyle,            onPressed:provider.order, icon: Icon(Icons              .align_vertical_bottom_outlined,color: Colors.black,), label: Text('Buyurtmalar',style: textStyle,),),          SizedBox(height: 20,),          ElevatedButton.icon(onPressed:()=>provider.menu(context), icon:          Icon            (Icons              .menu_book,color: Colors.black,), label: Text            ('Menu',style: textStyle,),style: buttonStyle,),        ],),),    );  }}