import 'package:flutter/material.dart';import 'package:mobile_bro_food_admin_panel/configs/text_configs.dart';import 'package:mobile_bro_food_admin_panel/pages/history/history_page.dart';import 'package:mobile_bro_food_admin_panel/pages/home/controller/home_controller.dart';import 'package:mobile_bro_food_admin_panel/pages/menu/menu_page.dart';import 'package:mobile_bro_food_admin_panel/pages/orders/order_page.dart';import 'package:provider/provider.dart';class HomePage extends StatelessWidget {  const HomePage({Key? key}) : super(key: key);  @override  Widget build(BuildContext context) {    return ChangeNotifierProvider<HomeProvider>(      create: (context) => HomeProvider(),      child: _Home(),    );  }}class _Home extends StatelessWidget {  @override  Widget build(BuildContext context) {    final provider = context.watch<HomeProvider>();    return Scaffold(      body: Column(        children: [          _cstomTabBar(),          Expanded(            flex: 10,            child: PageView(              physics: const NeverScrollableScrollPhysics(),              controller: provider.controller,              children: [             const   OrderPage(),               const  MenuPage(),                 HistoryPage(),              ],            ),          ),        ],      ),    );  }}class _cstomTabBar extends StatelessWidget {  final textStyle = const TextStyle(    color: Colors.black,    fontSize: 25,  );  final chtextStyle = const TextStyle(    color: Colors.white,    fontSize: 25,  );  final buttonStyle = ElevatedButton.styleFrom(      elevation: 0, backgroundColor: Colors.transparent);  @override  Widget build(BuildContext context) {    final provider = context.watch<HomeProvider>();    return Container(      width: MediaQuery.of(context).size.width,      height: 50,      color: Colors.orange,      child: SingleChildScrollView(          scrollDirection: Axis.horizontal,          child: Row(            mainAxisAlignment: MainAxisAlignment.center,            crossAxisAlignment: CrossAxisAlignment.center,            children: [              const SizedBox(                height: 20,              ),              ElevatedButton.icon(                style: buttonStyle,                onPressed: () => provider.order(0),                icon: Icon(                  Icons.align_vertical_bottom_outlined,                  color: provider.page == 0                      ? Colors.white                      : Colors.black,                ),                label: Text(                  activOrders,                  style: provider.page == 0 ? chtextStyle : textStyle,                ),              ),              const SizedBox(                height: 20,              ),              ElevatedButton.icon(                onPressed: () => provider.order(1),                icon: Icon(                  Icons.menu_book,                  color: provider.page == 1                      ? Colors.white                      : Colors.black,                ),                label: Text(menyu,                    style:                        provider.page == 1 ? chtextStyle : textStyle),                style: buttonStyle,              ),              ElevatedButton.icon(                onPressed: () => provider.order(2),                icon: Icon(                  Icons.history,                  color: provider.page == 2                      ? Colors.white                      : Colors.black,                ),                label: Text(                  history,                  style: provider.page == 2 ? chtextStyle : textStyle,                ),                style: buttonStyle,              ),            ],          )),    );  }}