import 'package:flutter/material.dart';import 'package:mobile_bro_food_admin_panel/pages/home/controller/home_controller.dart';import 'package:mobile_bro_food_admin_panel/pages/menu/menu_page.dart';import 'package:provider/provider.dart';class HomePage extends StatelessWidget {  const HomePage({Key? key}) : super(key: key);  @override  Widget build(BuildContext context) {    return ChangeNotifierProvider(create: (context)=> HomeProvider(),    builder: (_,__){      return _Home();    },    );  }}class _Home extends StatelessWidget{  @override  Widget build(BuildContext context) {    // TODO: implement build    return Scaffold(      body:          Column(children:  [            _cstomTabBar(),            IndexedStack(              index: context.read<HomeProvider>().page,              children: [                MenuPage(),          ],),        ],      ),    );  }}class _cstomTabBar extends StatelessWidget {  final textStyle =  const TextStyle(color: Colors.black,fontSize: 25,);  final chtextStyle =  const TextStyle(color: Colors.white,fontSize: 25,);  final buttonStyle = ElevatedButton.styleFrom(      elevation:0, backgroundColor: Colors.transparent  );  @override  Widget build(BuildContext context) {    final provider =   context.watch<HomeProvider>();    return Container(      width: MediaQuery.of(context).size.width,      height: 50,      color: Colors.orange,      child: SingleChildScrollView(          scrollDirection: Axis.horizontal,          child: Row(            mainAxisAlignment: MainAxisAlignment.center,            crossAxisAlignment: CrossAxisAlignment.center,            children: [              const SizedBox(height: 20,),              ElevatedButton.icon(                style: buttonStyle,                onPressed:()=>provider.order(0), icon:  Icon                (Icons                  .align_vertical_bottom_outlined,color: provider.page                  == 0?Colors.white:Colors                  .black,),                label: Text('Buyurtmalar',style: provider.page                    == 0?chtextStyle:textStyle,),),              const SizedBox(height: 20,),              ElevatedButton.icon(onPressed:()=>provider.order(1), icon:               Icon                (Icons                  .menu_book,color: provider.page                  == 1?Colors.white:Colors                  .black,), label: Text                ('Menu',style: provider.page                  == 1?chtextStyle:textStyle),style: buttonStyle,),              ElevatedButton.icon(onPressed:()=>provider.order(2),                icon:               Icon                (Icons                  .history,color:provider.page                  == 2?Colors.white:Colors                  .black,), label: Text                ('Activ Buyurtmalar',style: provider.page                == 2?chtextStyle:textStyle,),                style:              buttonStyle,), ElevatedButton.icon(onPressed:()              =>provider.order(3), icon:               Icon                (Icons                  .history_toggle_off,color:provider.page                  == 3?Colors.white:Colors                  .black,),                label: Text                ('Buyurtmalar tarixi',style: provider.page                  == 3?chtextStyle:textStyle,),style:              buttonStyle,),            ],)),    );  }}class _homePage extends StatelessWidget{  @override  Widget build(BuildContext context) {    // TODO: implement build    return ListView.builder(itemBuilder: (context,i){      return ListTile(title: Text('provider.list[i].name'),        subtitle: Text('provider.list[i].price'),leading:        Image.network          ('provider.list[i].image'),trailing: Text('provider.list[i].price'),);    });  }}