import 'package:flutter/material.dart';import 'package:mobile_bro_food_admin_panel/pages/detail/detail_page.dart';import 'package:mobile_bro_food_admin_panel/pages/menu/controller/menu_provider.dart';import 'package:provider/provider.dart';class MenuPage extends StatelessWidget {  const MenuPage({Key? key}) : super(key: key);  @override  Widget build(BuildContext context) {    print('MenuPage');    return ChangeNotifierProvider<MenuProvider>(        create: (context) => MenuProvider(),        child: const _MenuPage());  }}class _MenuPage extends StatelessWidget {  const _MenuPage({Key? key}) : super(key: key);  @override  Widget build(BuildContext context) {    print('MenuPage');    final provider = context.read<MenuProvider>();    print(provider.list.length);    return FutureBuilder(        future: provider.get(),        builder: (context, snapshot) {          print(snapshot.hasData);          print('MenuPageBuild');          return SizedBox(            height: MediaQuery.of(context).size.height * .8,            child: Stack(              children: [                _MenuView(),                Visibility(                  visible: provider.isLoading,                  child: const Center(                    child: CircularProgressIndicator(),                  ),                ),                ElevatedButton(                    onPressed: provider.closeOrOpen,                    child: const Text("Menyuga Yangi Taom Qoshish")),                Detail(),              ],            ),          );        });  }}class _MenuView extends StatelessWidget {  const _MenuView({    Key? key,  }) : super(key: key);  @override  Widget build(BuildContext context) {    final provider = context.watch<MenuProvider>();    return ListView.builder(        padding: const EdgeInsets.only(top: 50),        itemCount: provider.list.length,        itemBuilder: (context, i) {          return Card(            child: ListTile(              title: Text(provider.list[i].name),              subtitle: Row(                children: [                  Text(provider.list[i].price),                  Text(provider.list[i].price),                ],              ),              leading: Image.network(                provider.list[i].image,              ),              trailing: provider.list[i].have                  ? ElevatedButton(                style: ElevatedButton.styleFrom(backgroundColor:                Colors.green),                      onPressed: () {},                      child: Text(" Mavjud Emas"),                    )                  : ElevatedButton(                style: ElevatedButton.styleFrom(backgroundColor:                Colors.red),                      onPressed: () {},                      child: Text("Mavjud"),                    ),            ),          );        });  }}