import 'package:flutter/material.dart';import 'package:mobile_bro_food_admin_panel/pages/orders/controller/order_provider.dart';import 'package:provider/provider.dart';class OrderPage extends StatelessWidget {  const OrderPage({Key? key}) : super(key: key);  @override  Widget build(BuildContext context) {    return ChangeNotifierProvider(      create: (context)=> OrderProvider(),      child: _homePage(),    );  }}class _homePage extends StatelessWidget {  @override  Widget build(BuildContext context) {    final provider = context.watch<OrderProvider>();    print('Order');     if(provider.list.isEmpty){       return const Center(child: Text("Buyurtma mavjud emas"),);    }    return ListView.builder(        itemCount: provider.list.length,        itemBuilder: (context, i) {          return ListTile(            title:  Text(provider.list[i].numberPhone),            subtitle:  Text(provider.list[i].deliveryTime),            trailing:  Text(provider.list[i].comment),          );        });  }}