import 'package:flutter/material.dart';import 'package:mobile_bro_food_admin_panel/model/Order_model.dart';import 'package:mobile_bro_food_admin_panel/pages/orders/controller/order_provider.dart';import 'package:provider/provider.dart';class OrderPage extends StatelessWidget {  const OrderPage({Key? key}) : super(key: key);  @override  Widget build(BuildContext context) {    return ChangeNotifierProvider(      create: (context) => OrderProvider()..inits(),      child: _homePage(),    );  }}class _homePage extends StatelessWidget {  @override  Widget build(BuildContext context) {    final provider = context.watch<OrderProvider>();    print('Order Build');    print(provider.list.length);    if (provider.list.isEmpty) {      return const Center(        child: Text("Buyurtma mavjud emas"),      );    }    return ListView.builder(        itemCount: provider.list.length,        itemBuilder: (context, i) {        OrderModel order =  provider.list[i];          return Card(              child: ListTile(                title: Column(                  children: [                    Text(order.deliveryTime),                    Text(order.numberPhone),                    Text(order.token),                    Text(order.typePay),                    Text('addres'),                    Text(order.addres.first.float),                    Text(order.addres.first.home),                    Text(order.addres.first.longitude),                    Text(order.addres.first.latitude),                    Text(order.addres.first.floorCode),                    Text(order.addres.first.fromGeocoding),                    Text(order.addres.first.kvNumber),                  ],                ),              ));        });  }}