import 'package:flutter/cupertino.dart';import 'package:flutter/material.dart';import 'package:flutter/services.dart';import 'package:mobile_bro_food_admin_panel/pages/singin/provoider/signin_provoider.dart';import 'package:provider/provider.dart';class SignPage extends StatelessWidget {  const SignPage({Key? key}) : super(key: key);  static Page<void> page() =>      const MaterialPage<void>(child: SignPage());  @override  Widget build(BuildContext context) {    return Scaffold(      body: Padding(        padding: const EdgeInsets.all(8),        child:            ChangeNotifierProvider<SigninProvoider>(              create: (context) => SigninProvoider(),          child: const LoginForm(),        ),      ),    );  }}class LoginForm extends StatelessWidget {  const LoginForm({super.key});  @override  Widget build(BuildContext context) {    return ChangeNotifierProvider<SigninProvoider>(      create: (context) => SigninProvoider(),      builder: (context, _) {        double size = MediaQuery.of(context).size.width;        return Scaffold(          body: Stack(            children: [              SingleChildScrollView(                child: size > 700                    ? Row(                        children: [                          Expanded(                              flex: 2,                              child: Container(                                color: Colors.teal,                              )),                          Expanded(                            child: Container(                              padding: const EdgeInsets.all(25),                              child: Column(                                mainAxisAlignment:                                    MainAxisAlignment.start,                                crossAxisAlignment:                                    CrossAxisAlignment.center,                                children: [                                  const SizedBox(                                    height: 20,                                  ),                                  const _logoImg(),                                  const Text(                                    'Hello',                                    style: TextStyle(                                        fontSize: 55,                                        fontWeight: FontWeight.w700),                                  ),                                  const SizedBox(                                    height: 20,                                  ),                                  const Text(                                    'Sign in to your Account',                                    style: TextStyle(                                        fontSize: 20,                                        fontWeight: FontWeight.w400),                                  ),                                  const SizedBox(                                    height: 40,                                  ),                                  // #email                                  _EmailInput(),                                  const SizedBox(                                    height: 20,                                  ),                                  // #password                                  _PasswordInput(),                                  // #sign_in                                  const SizedBox(height: 8),                                  SizedBox(                                    height: MediaQuery.of(context)                                            .size                                            .height *                                        .1,                                  ),                                  _LoginButton(),                                  const SizedBox(height: 8),                                  SizedBox(                                    height: MediaQuery.of(context)                                            .size                                            .height *                                        .1,                                  ),                                  const SizedBox(                                    height: 20,                                  ),                                ],                              ),                            ),                          ),                        ],                      )                    : Container(                        padding: const EdgeInsets.all(25),                        child: Column(                          mainAxisAlignment: MainAxisAlignment.start,                          crossAxisAlignment:                              CrossAxisAlignment.center,                          children: [                            const SizedBox(                              height: 20,                            ),                            const _logoImg(),                            const Text(                              'Hello',                              style: TextStyle(                                  fontSize: 55,                                  fontWeight: FontWeight.w700),                            ),                            const SizedBox(                              height: 20,                            ),                            const Text(                              'Sign in to your Account',                              style: TextStyle(                                  fontSize: 20,                                  fontWeight: FontWeight.w400),                            ),                            const SizedBox(                              height: 40,                            ),                            // #email                            _EmailInput(),                            const SizedBox(                              height: 20,                            ),                            // #password                            _PasswordInput(),                            // #sign_in                            const SizedBox(height: 8),                            SizedBox(                              height:                                  MediaQuery.of(context).size.height *                                      .1,                            ),                            _LoginButton(),                            const SizedBox(height: 8),                            SizedBox(                              height:                                  MediaQuery.of(context).size.height *                                      .1,                            ),                            const SizedBox(                              height: 20,                            ),                          ],                        ),                      ),              ),            ],          ),        );      },    );  }}class _logoImg extends StatelessWidget {  const _logoImg({    Key? key,  }) : super(key: key);  @override  Widget build(BuildContext context) {    return Image.asset(      'assets/logo/img.png',      height: MediaQuery.of(context).size.height * .2,    );  }}class _EmailInput extends StatelessWidget {  @override  Widget build(BuildContext context) {    final provider = context.read<SigninProvoider>();    return Material(      borderRadius: BorderRadius.circular(123123),      elevation: 12.0,      shadowColor: Colors.grey.shade400,      child: TextFormField(        inputFormatters: [          FilteringTextInputFormatter.allow(            RegExp("[A-Za-z0-9]"),          ),        ],        controller: provider.emailController,        style: const TextStyle(fontSize: 18, color: Colors.black),        keyboardType: TextInputType.emailAddress,        textInputAction: TextInputAction.next,        autofocus: false,        decoration: InputDecoration(            border: InputBorder.none,            icon: const Icon(Icons.person, color: Colors.black),            hintText: 'Nikname',            fillColor: Colors.white,            filled: true,            contentPadding: const EdgeInsets.symmetric(                vertical: 10, horizontal: 20),            enabledBorder: OutlineInputBorder(                borderRadius: BorderRadius.circular(5.0),                borderSide: const BorderSide(                    color: Colors.white, width: 3.0))),      ),    );  }}class _PasswordInput extends StatelessWidget {  @override  Widget build(BuildContext context) {    final provider = context.read<SigninProvoider>();    return Material(      borderRadius: BorderRadius.circular(123123),      elevation: 12.0,      shadowColor: Colors.grey.shade400,      child: Consumer<SigninProvoider>(        builder: (context, _, s) {          return TextFormField(            controller: provider.passwordController,            textInputAction: TextInputAction.done,            obscureText: provider.passwordVisible,            autofocus: false,            decoration: InputDecoration(                suffixIcon: IconButton(                  icon: Icon(                    provider.passwordVisible                        ? Icons.remove_red_eye_rounded                        : CupertinoIcons.eye_slash,                    color: Colors.brown,                  ),                  onPressed: provider.visiblity,                ),                border: InputBorder.none,                icon: const Icon(CupertinoIcons.lock,                    color: Colors.black),                hintText: 'Password',                fillColor: Colors.white,                filled: true,                contentPadding: const EdgeInsets.symmetric(                    vertical: 10, horizontal: 20),                enabledBorder: OutlineInputBorder(                    borderRadius: BorderRadius.circular(5.0),                    borderSide: const BorderSide(                        color: Colors.white, width: 3.0))),            style: const TextStyle(fontSize: 18, color: Colors.black),            keyboardType: TextInputType.visiblePassword,          );        },      ),    );  }}class _LoginButton extends StatelessWidget {  @override  Widget build(BuildContext context) {    final provider = context.read<SigninProvoider>();    return Row(      mainAxisAlignment: MainAxisAlignment.end,      children: [        const Text(          "Sign In",          style: TextStyle(fontSize: 25, fontWeight: FontWeight.w600),        ),        const SizedBox(          width: 20,        ),        Container(          clipBehavior: Clip.hardEdge,          decoration: BoxDecoration(            color: Colors.orange,            borderRadius: BorderRadius.circular(40),            gradient: LinearGradient(                end: Alignment.bottomCenter,                begin: Alignment.topCenter,                colors: [                  Colors.orange.withOpacity(0.2),                  Colors.orange                ]),          ),          height: 50,          width: 65,          child: TextButton(            onPressed: () => provider.signIn(context),            child: const Icon(              CupertinoIcons.arrow_right,              color: Colors.white,              size: 33,            ),          ),        ),      ],    );  }}